            return self.rotate_right(node)

# Right Right Case
        if balance < -1 and val > node.right.val:
            return self.rotate_left(node)

        # Left Right Case
        if balance > 1 and val > node.left.val:
            node.left = self.rotate_left(node.left)
            return self.rotate_right(node)

        # Right Left Case
